def create_polar_plot(i, fig, amps, title):
    theta = np.linspace(0, 2*np.pi, len(amps))  # angles
    ax = fig.add_subplot(2, 3, i+1, projection='polar')
    ax.plot(theta, amps)
    ax.grid(True)
    ax.set_title(title, va='center')


